    let apiKey = "6697611895f9d8bb5ac23403332f6cdd";
    let units = "imperial";
    let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${coordinates.lat}&lon=${coordinates.lon}&appid=${apiKey}&units=${units}`;
    axios.get(apiUrl).then(displayForecastWeek);
  }

  let header = (
    <header>
      <div className="citynames">
        <div className="row">
          <div className="col cityname">
            <p>
              <a href="link" onClick={searchMoscow}>
                Moscow
              </a>
            </p>
          </div>
          <div className="col cityname">
            <p>
              <a href="link">Berlin</a>
            </p>
          </div>
          <div className="col cityname">
            <p>
              <a href="link">London</a>
            </p>
          </div>
          <div className="col cityname">
            <p>
              <a href="link">New York</a>
            </p>
          </div>
        </div>
      </div>
    </header>
  );
  let forecast = <h3>Hello</h3>;
  let form = (
    <form onSubmit={handleSubmit}>
      <input
        type="search"
        placeholder="Enter a city..."
        onChange={updateCity}
      />
      <button type="Submit">Search</button>
      <button type="Submit" onClick={searchCurrentLocation}>
        📍 Current location
      </button>
      <button type="Submit" onClick={searchRandomLocation}>
        🗺 Random location
      </button>
    </form>
  );

  if (loaded) {
    return (
      <body>
        <div className="container">
          {header}
          {form}
          <h2 className="message">{message}</h2>

          <div className="row">
            <h5 className="lastUpdate">Last update:</h5>
          </div>
          <div className="row">
            <div className="col">
              <h4>
                {currentDay} {currentTime}
              </h4>
              <h4 className="description">{weather.description}</h4>
              <h2>
                <img src={weather.icon} alt={weather.description} />
                <span className="currentDegrees">
                  {Math.round(weather.temperature)}°C
                </span>
              </h2>
            </div>
            <div className="col">
              <h4>Humidity: {weather.humidity}%</h4>
              <h4>Wind: {weather.wind}m/sec</h4>
            </div>
          </div>
          <div className="forecast">
            <h3>The next 6 days</h3>
            <div className="hours">Hello</div>
            <div className="week">{forecast}</div>
          </div>
        </div>
      </body>
    );
  } else {
    return (
      <body>
        <div className="container">
          {header}
          {form}
        </div>
      </body>
    );
  }
}
